COPY(SELECT CONCAT('https://www.gis-hub.ca/dataset/', name) AS url, title, maintainer_email, initcap(split_part(split_part(maintainer_email, '.', 1), '@', 1)) AS maintainer_name, DATE_PART('day', NOW() - metadata_modified::timestamp) AS days_since_modified FROM public.package WHERE state='active' AND maintainer_email='cole.fields@dfo-mpo.gc.ca' ORDER BY days_since_modified DESC) TO '/tmp/metadata_modified.csv' WITH CSV HEADER DELIMITER ',';
